mapper.select:
select b.serial,b.userNo,b.vipLevel,b.vipLevelZN,b.balance,a.accumulative,a.operation,b.qqAccount,b.phoneNo,b.createTime,a.state, b.loginTime,a.contacts,a.companyName,b.status,a.updateTime,a.planTime,a.feedback,a.remark,a.discard,c.empName from user b left JOIN userinfo a ON a.userNo = b.userNo left join emp c ON a.empNo = c.empNo where 1=1
and a.discard is null or a.discard=0
and c.empNo=#{empNo,jdbcType=VARCHAR} and a.discard=0
and b.userNo like concat('%',#{userNo},'%')
and a.updateTime between #{FirstCreateTime} and #{LastCreateTime}
and a.updateTime between #{FirstCreateTime} and now()
and b.status=#{status}
and a.state=#{state}
order by b.createTime desc
limit #{offset},#{limit}

select b.serial,b.userNo,b.vipLevel,b.vipLevelZN,b.balance,a.accumulative,a.operation,b.qqAccount,b.phoneNo,b.createTime,a.state,
b.loginTime,a.contacts,a.companyName,b.status,a.updateTime,a.planTime,a.feedback,a.remark,a.discard,c.empName
from user b  left JOIN  userinfo a ON a.userNo = b.userNo
left join emp c ON a.empNo = c.empNo
where 1=1
and a.discard=1

    and  c.empNo=#{empNo,jdbcType=VARCHAR}


    and b.userNo like concat('%',#{userNo},'%')


    and a.updateTime between #{FirstCreateTime} and #{LastCreateTime}


    and a.updateTime between #{FirstCreateTime} and now()


    and b.status=#{status}


    and a.state=#{state}

order by b.createTime desc

    limit #{offset},#{limit}

select b.serial,b.userNo,b.vipLevel,b.vipLevelZN,b.balance,a.accumulative,a.operation,b.qqAccount,b.phoneNo,b.createTime,a.state, b.loginTime,a.contacts,a.companyName,b.status,a.updateTime,a.planTime,a.feedback,a.remark,a.discard,c.empName from user b left JOIN userinfo a ON a.userNo = b.userNo left join emp c ON a.empNo = c.empNo where 1=1
and a.discard is null or a.discard=0
and c.empNo=#{empNo,jdbcType=VARCHAR} and a.discard=0
and b.userNo like concat('%',#{userNo},'%')
and b.status=#{status}
and a.state=#{state}
order by b.logintime desc
limit #{offset},#{limit}
